INSERT INTO `ai_about` (`id`, `content`, `createtime`) VALUES
(1, '<p>&nbsp;</p><p style="margin-top:10px;color:rgb(101, 101, 101);font-family:Tahoma Arial, sans-serif, Helvetica;">爱健美健美健康信息分享学习平台，简称爱健美，是加伦信息科技(广州)有限公司旗下的一个项目产品。爱健美网站（<a target="_blank" style="color:rgb(71, 174, 232);cursor:pointer;" href="http://dev.aijianmei.com/www.aijianmei.com">www.aijianmei.com</a>），为一个专门为健身运动爱好者打造的一个网络社交信息学习交流平台。</p><p style="margin-top:10px;color:rgb(101, 101, 101);font-family:Tahoma Arial, sans-serif, Helvetica;">爱健美平台包括爱健美网站（<a target="_blank" style="color:rgb(71, 174, 232);cursor:pointer;" href="http://dev.aijianmei.com/www.aijianmei.com">www.aijianmei.com</a>），ios 客户端（iPhone ，iPad） 以及Andriod 平台下的手机以及平板电脑客户端。</p><p style="margin-top:10px;color:rgb(101, 101, 101);font-family:Tahoma Arial, sans-serif, Helvetica;">爱健美主要面向的是热爱健身运动或者是对健身运动有浓厚兴趣并且期望得到更完善的健美指导的用户。该平台将会向大众传递，最新，最科学，风靡全美国健身健美健康知识。其网站内容以高清（HD）健身视频，科学的健身教程，文章的形式展示给用户。通过爱健美平台，用户将会了解和学习到适合自己的健康健美方法和知识，享受运动带来的乐趣。</p>', 1365558053);
