INSERT INTO `ai_video` (`id`, `uid`, `category_id`, `title`, `brief`, `link`, `htmlurl`, `wapurl`, `create_time`, `status`, `like`, `unlike`, `click`) VALUES
(19, 1, 53, '头前杠铃深蹲后推举', ' 无论是谁都可以从深蹲运动中获益，而且益处是显而易见的。通过深蹲你整个身体都在获得力量，特别是你的躯干还有下身。', 'http://player.youku.com/player.php/sid/XNTM2MTg5MDc2/v.swf', NULL, NULL, 1364889948, 1, 0, 0, 22),
(18, 1, 53, '双膝前屈跳', '双膝前屈跳，详情请看视频动作示范。', 'http://player.youku.com/player.php/sid/XNTM2NzAxMjg4/v.swf', NULL, NULL, 1364889788, 1, 0, 0, 23),
(17, 1, 53, '杠铃卧推', '详情请看视频动作示范。', 'http://player.youku.com/player.php/sid/XNTM2MTg2NzMy/v.swf', NULL, NULL, 1364889541, 1, 0, 0, 39),
(28, 1, 53, '单手抓举哑铃', '收缩握铃一臂的二头肌将前臂向上弯起，到可能的最高点时，彻底收缩二头肌一秒钟，然后伸展肘关节，让哑铃下落到开始位置。', 'http://player.youku.com/player.php/sid/XNTM3MzI4MjAw/v.swf', NULL, NULL, 1365146129, 1, 0, 0, 21),
(20, 1, 53, '俯卧撑', '双手支撑身体，双臂垂直于地面，两腿向身体后方伸展，依靠双手和两个脚的脚尖保持平衡，保持头、脖子、后背、臀部以及双腿在一条直线上，动作重点：全身挺直，平起平落。', 'http://player.youku.com/player.php/sid/XNTM2MTM0Mzg0/v.swf', NULL, NULL, 1364890081, 1, 0, 0, 176),
(21, 1, 53, '头前哑铃深蹲后推举', '深蹲与哑铃的搭配，大腿肌肉是人体最大的肌肉群，练好大腿，增肌事半功倍，也能为上身的训练打下基础，哑铃深蹲后推举是练习大腿的重要动作， 主要增强大腿股四头肌，值得注意的是此动作对臀肌的增长也很有效。', 'http://player.youku.com/player.php/sid/XNTM2MDM0MjY4/v.swf', NULL, NULL, 1364890688, 1, 0, 0, 31),
(22, 1, 53, '健腹滑轮运动', '将膝盖放在跪垫上双手紧握健腹轮手柄向前推动健腹轮至身体水平于地面然后回收归位反复操作。', 'http://player.youku.com/player.php/sid/XNTM2MTE2Njgw/v.swf', NULL, NULL, 1364890730, 1, 0, 0, 23),
(23, 1, 53, '头后杠铃反向弓步走', '详情请看视频动作示范。', 'http://player.youku.com/player.php/sid/XNTM0OTY0ODc2/v.swf', NULL, NULL, 1364890837, 1, 0, 0, 20),
(24, 1, 53, '日常锻炼', '详情请看视频动作示范。', 'http://player.youku.com/player.php/sid/XNTM0OTE2MTM2/v.swf', NULL, NULL, 1364890877, 1, 0, 0, 22),
(39, 1, 53, '保加利亚深蹲', '保加利亚蹲，被称为保加利亚举重队的力量秘诀,估计是力量界最负盛名的训练方法了，3位3届奥运举重冠军Suleymanoglu、Dimas、Mutlu和抓举世界纪录保持者Krastev都受益于该方法。', 'http://player.youku.com/player.php/sid/XNTM4MjA3MzY0/v.swf', 'http://v.youku.com/v_show/id_XNTM4MjA3MzY0.html', '', 1365147720, 1, 0, 0, 58),
(25, 1, 53, '弓步跳', '在此运动中，开始时你就要以一个标准的冲刺的姿势起跳，然后变换双脚的姿势。这是一个很好的运动，它可以帮助保证你的腿部强壮，有平衡性而且有协调性。', 'http://player.youku.com/player.php/sid/XNTM2NzY3MjAw/v.swf', NULL, NULL, 1364973247, 1, 0, 0, 28),
(26, 1, 53, '登山跑', '详情请看视频动作示范。', 'http://player.youku.com/player.php/sid/XNTM2NzI1NTMy/v.swf', NULL, NULL, 1364973376, 1, 0, 0, 81),
(27, 1, 53, '站姿曲杆杠铃弯举', '反握杠铃，两手距离与肩膀同宽，膝盖略微弯曲。保持身体不动，用力弯举杠铃至胸前，然后缓慢放下，注意不要接触到大腿', 'http://player.youku.com/player.php/sid/XNTM2NzA4NTM2/v.swf', NULL, NULL, 1364973466, 1, 0, 0, 52),
(29, 1, 53, '上斜哑铃飞鸟', '注意保持绷紧胸部，把意念集中胸大肌。飞鸟动作一定要缓缓做，下铃时尤其如此。', 'http://player.youku.com/player.php/sid/XNTM3MzM1MDQ4/v.swf', NULL, NULL, 1365146256, 1, 0, 0, 23),
(30, 1, 53, '哑铃卧推', '仰卧在平的卧推凳上，两脚平踏在地上。两肘弯曲，握住哑铃，拳眼相对，手心朝腿部的方向，哑铃的轴线位于乳头上方1厘米处（胸肌中部），抵住胸部。', 'http://player.youku.com/player.php/sid/XNTM3MzM1MTMy/v.swf', NULL, NULL, 1365146346, 1, 0, 0, 19),
(31, 1, 53, '半蹲蹬脚单手哑铃推举', '详情请看视频动作示范。', 'http://player.youku.com/player.php/sid/XNTM3MzMzNDA0/v.swf', NULL, NULL, 1365146416, 1, 0, 0, 21),
(32, 1, 53, '俯卧侧平举', '双脚撑踏地面，稳固维持身体体平衡。双臂肘关节微屈，双手握哑铃垂于肩垂线上。三角肌后束用力，牵拉双臂侧上举哑铃至上臂与肩(即与地面)平行，有意识体察目标肌“顶峰收缩”约1秒钟，然后意念强化肌肉感觉，慢慢', 'http://player.youku.com/player.php/sid/XNTM3MzMzNDUy/v.swf', NULL, NULL, 1365146551, 1, 0, 0, 24),
(33, 1, 53, '上斜哑铃推举', '双腿撑住地面，双手抓牢哑铃。双腿撑住地面，双手抓牢哑铃使哑铃平行举起。', 'http://player.youku.com/player.php/sid/XNTM3MzM0NTAw/v.swf', NULL, NULL, 1365146584, 1, 0, 0, 20),
(34, 1, 53, '哑铃侧平举', '双臂侧平举，直到上臂与地面平行，同上身垂直,此时保持同样的向后曲肘角度和姿势,稍稍停顿，然后缓缓下铃，回复到起始位臵.', 'http://player.youku.com/player.php/sid/XNTM3MzMzOTcy/v.swf', NULL, NULL, 1365146605, 1, 0, 0, 20),
(35, 1, 53, '双手拉力器直臂下压', '手臂下拉，手把下拉到大腿跟前。然后返回到起始位置，再重复动作。', 'http://player.youku.com/player.php/sid/XNTM3MzM1NzI0/v.swf', NULL, NULL, 1365146760, 1, 0, 0, 23),
(36, 1, 53, '哑铃单手划船', '一手拿哑铃，另一边手扶在凳上。 弯腰使背部拱起，保持下半身平衡。', 'http://player.youku.com/player.php/sid/XNTM3MzMzNzY4/v.swf', 'http://v.youku.com/v_show/id_XNTM3MzMzNzY4.html', '', 1365146782, 1, 0, 0, 33),
(37, 1, 53, '拉力器正握下拉', '自然站立，两脚间距和肩同宽，挺胸收腹紧腰。右臂向下伸直置于体侧，掌心向前握住把柄一端。  ', 'http://player.youku.com/player.php/sid/XNTM3MzM1MzQ4/v.swf', 'http://v.youku.com/v_show/id_XNTM3MzM1MzQ4.html', '', 1365146801, 1, 0, 0, 38),
(38, 1, 53, '上斜肱二头肌哑铃弯举', '当持铃弯起时，腰背部不要放松。当持铃弯起至胸前时，使肱二头肌尽量收紧，并保持静止3秒钟。然后，再慢慢放下。也可以立姿进行。', 'http://player.youku.com/player.php/sid/XNTM3MzM0OTA4/v.swf', 'http://v.youku.com/v_show/id_XNTM3MzM0OTA4.html', '', 1365146822, 1, 0, 0, 47),
(40, 1, 53, '杠铃耸肩', '双眼注视正前方，保持身体姿势的同时吸气，屏住呼吸，手臂伸直，将双肩抬得越高越好，继续屏住呼吸，并在最高点位置处用力收缩斜方肌。', 'http://player.youku.com/player.php/sid/XNTM4MjA4ODg0/v.swf', 'http://v.youku.com/v_show/id_XNTM4MjA4ODg0.html', '', 1365147740, 1, 0, 0, 62),
(41, 1, 53, '杠铃直立抓举至下巴', '详情请看视频动作示范。', 'http://player.youku.com/player.php/sid/XNTM4MjA4NjAw/v.swf', 'http://v.youku.com/v_show/id_XNTM4MjA4NjAw.html', '', 1365147760, 1, 0, 0, 67),
(42, 1, 53, '杠铃蹲举', '与肩同宽的杠铃颈后蹲举动作发展的主要肌群为臀大肌、臀中肌、股四头肌和部分内收肌等，参与的肌群多.', 'http://player.youku.com/player.php/sid/XNTM4MjA4OTcy/v.swf', 'http://v.youku.com/v_show/id_XNTM4MjA4OTcy.html', '', 1365147778, 1, 0, 0, 83),
(43, 1, 53, '弯腰杠铃拉举', '宽距站姿，双手正握，握距比肩稍宽，双臂完全伸直；微微屈膝，从臀部屈背，保持身体成45度角不变；持铃在身前，稍稍低于膝盖。', 'http://player.youku.com/player.php/sid/XNTM4MjEwMDE2/v.swf', 'http://v.youku.com/v_show/id_XNTM4MjEwMDE2.html', '', 1365147814, 1, 0, 0, 182);
